mapper.select:
select b.* , count(if(ls.type='L', ls.type, null)) likecnt from board b
join like_siren ls on b.board_seq = ls.board_seq
where b.board_seq = #{board_seq}

mapper.update:
update board
set
content=#{content}, img=#{img}, public_yn=#{public_yn}
where board_seq=#{board_seq}

mapper.delete:
delete board
where
writer=#{writer} and board_seq=#{board_seq}

mapper.select:

select * from like_siren
where board_seq = #{board_seq}
and sender = #{sender}
and type = 'L'
union
select * from like_siren
where board_seq = #{board_seq}
and sender = #{sender}
and type = 'S'

mapper.delete:
delete
from like_siren
where board_seq = #{board_seq} and sender = #{sender}

mapper.insert:
INSERT INTO like_siren (`board_seq`, `sender`, `type`, `date`) 
VALUES (#{board_seq},  #{sender}, 'L', now())
ON DUPLICATE KEY UPDATE board_seq=#{board_seq}, sender=#{sender}, type='L', date=now();

INSERT INTO like_siren (`board_seq`, `sender`, `type`, `date`) 
VALUES (#{board_seq},  #{sender}, 'S', now())
ON DUPLICATE KEY UPDATE board_seq=#{board_seq}, sender=#{sender}, type='S', date=now();
