mapper.select:
select e.* from ( select @rownum:=@rownum+1 As row , d.board_seq , d.img from ( ( select a.board_seq , a.img , a.posted from ( select * from board where board_seq in( select aa.board_seq from ( select board_seq , count(sender) sender from like_siren where sender in ( select receiver from relationship where sender = #{id} and status in ('y','r') ) and date >= date_add(now(), interval -7 day) group by board_seq order by sender desc ) aa ) and public_yn = 'y' ) a )
union
( select c.board_seq , c.img , c.posted from ( select b.board_seq , b.img ,b.posted from (select * from board where board_seq in( select a.board_seq from (select board_seq, count(sender) AS likecnt from like_siren where board_seq in( select board_seq from board where posted >= date_add(now(), interval -1 month) and public_yn = 'y' order by posted desc ) group by board_seq order by likecnt desc) a ) order by field(board_seq, 'select a.board_seq from (select board_seq, count(sender) AS likecnt from like_siren where board_seq in( select board_seq from board where posted >= date_add(now(), interval -1 month) and public_yn = ''y'' order by posted desc ) group by board_seq order by likecnt desc) a ' )) b , (select @rownum:=0) tmp )c ) ) d order by d.posted desc , (select @rownum:=0) )e limit #{row},9;
